Module.Content:
        
        #requireResource("com.atlassian.jira.gadgets:common")
        #requireResource("com.atlassian.gadgets:common-resources")
        #requireResource("com.atlassian.jira.gadgets:autocomplete")
        #requireResource("org.everit.jira.timetracker.plugin:jttp_worklog_summary_resources")
        #includeResources()
   
      <script type="text/javascript">
    (function () {
        var gadget = AJS.Gadget({
             baseUrl: "__ATLASSIAN_BASE_URL__",
             config: {
                       descriptor:worklogSummaryGadget.getDescriptor,
                       
                      },
              view: {
                enableReload: true,
                onResizeAdjustHeight: true,
                template: worklogSummaryGadget.getTemplate,
                args: [{key: "selectedFilter",
                        ajaxOptions: function() {
                           var jqlId=this.getPref("filterId").replace("filter-","");
                           return {
                                  headers: { 
                                          'Accept': 'application/json',
                                          'Content-Type': 'application/json' 
                                      },
                                     url: "/rest/api/2/filter/"+jqlId,
                                     contentType: "application/json"
                                     }
                               }
                        },
                      {key: "worlogSummaryData",
                          ajaxOptions: function() {
                          var jqlId=this.getPref("filterId").replace("filter-","");
                          var period=this.getPref("period");
                          
                          return {
                           url: "/rest/jttp-rest/1/worklog-summary-gadget/getWorklogSummaryData?jqlId="
                           +jqlId
                           +"&period="+period+"&display="+this.getPref("display")
                           +"&groupBy="+this.getPref("groupBy")
                          };
                       }
                     }
                ]
        }
      })

  })();
      </script>
